import { takeEvery, call, put } from "redux-saga/effects";
import {
  firestore,
  convertCollectionsSnapshotToMap,
} from "../../firebase/firebase.utils";

import { fetchColletionsSucces, fetchColectionsFailure } from "./shop.actions";
import ShopActionsTypes from "./shop.types";

export function* fetchCollectionsAsync() {
  yield console.log("I am fired");
  try {
    const collectionRef = firestore.collection("collections");
    const snapshot = yield collectionRef.get();
    const collectionsMap = yield call(
      convertCollectionsSnapshotToMap,
      snapshot
    );
    yield put(fetchColletionsSucces(collectionsMap));
  } catch (error) {}
  // collectionRef
  //   .get()
  //   .then((snap) => {
  //     const collectionsMap = convertCollectionsSnapshotToMap(snap);
  //     dispatch(fetchColletionsSucces(collectionsMap));
  //   })
  //   .catch((error) => dispatch(fetchColectionsFailure(error.message)));
}
export function* fetchCollectionsStart() {
  yield takeEvery(
    ShopActionsTypes.FETCH_COLLECTIONS_START,
    fetchCollectionsAsync
  );
}
